    setIsOpen(!isOpen);
  };

  return (
    <>
      <div className="fixed bottom-4 right-4 cursor-pointer">
        <button
          onClick={handleChatClick}
          className="bg-green-500 hover:bg-green-700 text-white rounded-full p-4 shadow-lg"
        >
